import { createApi } from '@reduxjs/toolkit/query/react'
import { instance } from './instance';

const axiosBaseQuery = () => async ({url, method, data, find}) => {
  try {
    const res = await instance({method,url,data})
    if (find) return {data:res.data.filter(todo => todo.Todoid === find)}
    return {data:res.data}
  } catch (error) {
    return console.log("에러", error);
  }
}

const todortkquery = createApi({
  baseQuery : axiosBaseQuery(),
  tagTypes: ["TODOS"],
  endpoints : builder => ({
    getTodoRTK : builder.query({
      query: () => ({url:"/todos", method:"get"}),
      providesTags: ['TODOS'],
    }),
    postTodoRTK : builder.mutation({
      query: (payload) => ({url:"/todos", method:"post", data:payload}),
      invalidatesTags: ['TODOS']
    }),
    deleteTodoRTK : builder.mutation({
      query: (payload) => ({url:`/todos/${payload}`, method:"delete"}),
      invalidatesTags: ['TODOS']
    }),
    updateTodoRTK : builder.mutation({
      query: (payload) => {
        console.log(payload.content);
        return ({url:`/todos/${payload.id}`, method:"patch", data:{content:payload.content}})
      },
      invalidatesTags: ['TODOS']
    }),

    // 댓글관련 부분 
    getCommentRTK : builder.query({
      query: (payload) => ({url:`/comments`, method:"get", comment:payload}),
      providesTags: ['COMMENT']
    }),

    postTodoCommentRTK : builder.mutation({
      query: (payload) => {
        console.log(payload);
        return ({url:`/comments`, method:"post", data:payload})
      },
      invalidatesTags : ['COMMENT']
    }),

    // 상세페이지에서의 조회
    getFindTodoRTK : builder.query({
      query: (id) => ({url:`/todos/${id}`, method:"get"}),
      providesTags: (result) => [{type:"TODOS", id:result.id}]
    }),
  })
})

export const {useGetTodoRTKQuery, usePostTodoRTKMutation, useDeleteTodoRTKMutation, useUpdateTodoRTKMutation, useGetFindTodoRTKQuery, useGetCommentRTKQuery, usePostTodoCommentRTKMutation} = todortkquery
export default todortkquery
