import React from 'react'
import { css, styled } from 'styled-components';

function NewTodos() {
  const onSubmitHandler = (e) => {
    e.preventDefault()
    console.log("동작");
  }
  return (
    <form onSubmit={onSubmitHandler}>
      <h2>작성자</h2>
      <CustomInput type='text' placeholder='작성자의 이름을 입력해주세요.(5자 이내)' maxLength={5}/>
      <h2>제목</h2>
      <CustomInput type='text'placeholder='제목을 입력해주세요.(50자 이내)' maxLength={20}/>
      <h2>내용</h2>
      <CustomInput as="textarea" type='text' placeholder='내용을 입력해주세요.(200자 이내)' maxLength={200}/>
      <CustomInput type='submit' value="추가하기"/>
    </form>
  )
}

export default NewTodos

const CustomInput = styled.input`
  box-sizing: border-box;
  width: 100%;
  border-radius: 10px;
  border: 1px solid black;
  padding: 10px;
  height: 50px;

  ${({as}) => as === "textarea"  && css`
    resize: none;
    height: 200px;
  `}
  ${({type}) => type === "submit" && css`
    ba
  `}

`
