import React from 'react'
import {styled} from 'styled-components'

function App() {
  return (
    <HomeLayout>
      {/* 공용헤더 */}
      <Header>
        <HeaderSectionL>홈</HeaderSectionL>
        <HeaderSectionR>모두의 투두리스트</HeaderSectionR>
      </Header>

      {/* 공용 HOME 본문 */}
      <br/>
      <h2>무엇을 할까요?</h2>
      <HomeActicle>할일 기록하기</HomeActicle>
      <HomeActicle>TODO LIST</HomeActicle>


    </HomeLayout>
  )
}

export default App

const HomeLayout = styled.div`
  box-sizing: border-box;
  max-width: 1200px;
  height: 100px;
  margin: 0 auto;
  border: 1px solid black;
`

const Header = styled.header`
  box-sizing: border-box;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`

const HeaderSectionL = styled.section`
  box-sizing: border-box;
  display: flex;
  justify-content: start;
  align-items: center;
  flex: 1 1 0;
  padding: 20px;
  font-size: 2rem;
  font-weight: 800;
`

const HeaderSectionR = styled(HeaderSectionL)`
  justify-content: end;
`

const HomeActicle = styled.article`
  box-sizing: border-box;
  margin: 0 auto;
  width: 100%;
  height: 50px;
  border: 1px solid orange;
  margin: 10px 0;
  border-radius: 10px;
  display: flex;
  justify-content: center;
  align-items: center;

  &:hover {
    color: white;
    background-color: orange;
  }
`
