const todoSlice = createSlice({
  name:"todoSlice",
  initialState:[],
  extraReducers: builder => {
    builder
    .addCase(__getTodosThunk.pending, (state) => {
      state.isLoading = true;
      state.isError = "";
    })
  }
})

