import React, { useEffect, useRef } from "react";

import { RTKqueryForm, TodosBox } from "../styled";
import { useThunk } from "../hooks/useThunk";
import { useSetTodoRTKMutation } from "../api/todoRTKquery";

const TodosRTK = ({ todoid, title }) => {
  const {
    onChangeupdate,
    newtodos,
    update,
  } = useThunk(todoid, title);

  const inputRef = useRef(null);

  useEffect(() => {
    update && inputRef.current.focus();
  }, [update]);


  const [setTodoRTK] = useSetTodoRTKMutation()
  const onUpdateSubmitHandler = (e) => {
    e.prevnetDefault()
    setTodoRTK({id:todoid, title:newtodos})
  }




  return (
    <TodosBox>
      <button>삭제</button>
      <button>수정</button>
      <p>{title}</p>
      {update && (
        <RTKqueryForm
          onSubmit={onUpdateSubmitHandler(todoid)}
          $state="update"
          $position={update}
        >
          <input
            type="text"
            onChange={onChangeupdate}
            maxLength={10}
          />
        </RTKqueryForm>
      )}
    </TodosBox>
  );
};

export default TodosRTK;
