import React, { useEffect, useRef, useState } from "react";
import { useDispatch } from "react-redux";
import {
  __deleteTodosThunk,
  __updateTodosThunk,
} from "../redux/modules/todoSlice";
import { TodosBox, UpdateForm } from "../styled";

const Todos = ({ todoid, title }) => {
  const inputRef = useRef(null);
  const dispatch = useDispatch();

  // AXIOS DELETE 기능구현 : CRUD (3) DELETE
  const onDeleteHandler = async () => {
    dispatch(__deleteTodosThunk(todoid)); // THUNK // AXIOS GET 기능구현 : CRUD (1) READ // (3) 서버와 동기화
  }; // 리렌더링 발생

  // AXIOS UPDATE 기능구현 : CRUD (4) UPDATE
  const [update, setUpdate] = useState(false); // 조건부 렌더링

  const onUpdateHandler = () => {
    setUpdate((pre) => !pre);
  };

  const [updatetodos, setUpdateTodos] = useState(title); // 수정할 title의 valuse
  const onChangeInput = (e) => {
    setUpdateTodos(e.target.value);
  };

  const onSubmitHandler = () => async (e) => {
    e.preventDefault();
    dispatch(
      __updateTodosThunk({ id: todoid, content: { title: updatetodos } })
    );
    setUpdate((pre) => !pre);
  }; // 리렌더링 발생

  useEffect(() => {
    update && inputRef.current.focus();
  }, [update]);

  return (
    <TodosBox>
      <button onClick={onDeleteHandler}>삭제</button>
      <button onClick={onUpdateHandler}>수정</button>
      <p>{title}</p>
      {update && (
        <UpdateForm
          onSubmit={onSubmitHandler(todoid)}
          $state="update"
          $position={update}
        >
          <input
            ref={inputRef}
            value={updatetodos}
            type="text"
            onChange={onChangeInput}
            maxLength={10}
          />
        </UpdateForm>
      )}
    </TodosBox>
  );
};

export default Todos;
