import React, { useEffect, useRef, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { __deleteTodosThunk, __getTodosThunk, __poseTodosThunk, selecttodoSlice } from './redux/modules/todoSlice'
import { Form } from './styled'
import Todos from './components/Todos'

/* App.js ------------------------------------------------------------------- */

function App() {
  // THUNKAPI
  const dispatch = useDispatch()
  const {todos:todoSlice} = useSelector(selecttodoSlice)

  useEffect(()=>{
    dispatch(__getTodosThunk()) 
  },[dispatch])

  const [newtodos, setNewTodos] = useState("")
  const onChangeInput = (e) => {
    setNewTodos(e.target.value)
  }

  const onSubmitHandler = async (e) => {
    e.preventDefault()
    dispatch(__poseTodosThunk({id:Date.now(), title:newtodos})) // THUNK // AXIOS GET 기능구현 : CRUD (1) READ // (3) 서버와 동기화 
    setNewTodos("")
  } // 리렌더링 발생

  return (
    <div>
      <p>JSON-Server</p>
      <Form onSubmit={onSubmitHandler}>
        <input value={newtodos} type='text' onChange={onChangeInput} maxLength={10}/>
      </Form>
      {todoSlice && todoSlice.map(({id, title}) => <Todos key={id} todoid={id} title={title}/>)}
    </div>
  )
}

export default App
