import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { __getTodosThunk, __poseTodosThunk, selecttodoSlice } from './redux/modules/todoSlice'
import { Form } from './styled'
import Todos from './components/Todos'

/* App.js ------------------------------------------------------------------- */

function App() {
  const {todoSlice, newtodos, dispatch, onSubmitHandler, onChangeInput} = use

  useEffect(()=>{
    dispatch(__getTodosThunk()) 
  },[dispatch])

  return (
    <div>
      <p>JSON-Server</p>
      <Form onSubmit={onSubmitHandler}>
        <input value={newtodos} type='text' onChange={onChangeInput} maxLength={10}/>
      </Form>
      {todoSlice && todoSlice.map(({id, title}) => <Todos key={id} todoid={id} title={title}/>)}
    </div>
  )
}

export default App
