import React, { useEffect, useRef, useState } from 'react'
import axios from 'axios'
import {styled} from 'styled-components'
import { useDispatch, useSelector } from 'react-redux'
import { __getTodosThunk, __poseTodosThunk, selecttodoSlice } from './redux/modules/todoSlice'

/* AXIOS ------------------------------------------------------------------ */

export const instance = axios.create({
  baseURL:process.env.REACT_APP_SERVERKEY1
}) // todos // 인스턴스 클래스(axios.create 메소드를 통해서)의 결과로 생성된 객체(하나의 baseURL 객체)를 인스턴스 불러요. 

/* App.js ------------------------------------------------------------------- */

function App() {
  // THUNKAPI
  const dispatch = useDispatch()
  const {todos:todoSlice} = useSelector(selecttodoSlice)

  useEffect(()=>{
    dispatch(__getTodosThunk()) // THUNK // (3) 서버와 동기화 
  },[dispatch])

  // AXIOS POST 기능구현 : CRUD (2) CREATE
  const [newtodos, setNewTodos] = useState("")
  const onChangeInput = (e) => {
    setNewTodos(e.target.value)
  }

  const onSubmitHandler = async (e) => {
    e.preventDefault()
    dispatch(__poseTodosThunk({id:Date.now(), title:newtodos})) // THUNK // AXIOS GET 기능구현 : CRUD (1) READ // (3) 서버와 동기화 
    setNewTodos("")
  } // 리렌더링 발생

  return (
    <div>
      <p>JSON-Server</p>
      <Form onSubmit={onSubmitHandler}>
        <input value={newtodos} type='text' onChange={onChangeInput} maxLength={10}/>
      </Form>
      {todoSlice && todoSlice.map(({id, title}) => <Todos key={id} todoid={id} title={title}/>)}
    </div>
  )
}

export default App


const Todos = ({todoid, title}) => {
  const dispatch = useDispatch()

  // AXIOS DELETE 기능구현 : CRUD (3) DELETE
  const onDeleteHandler = async () => {
    dispatch(__poseTodosThunk()) // THUNK // AXIOS GET 기능구현 : CRUD (1) READ // (3) 서버와 동기화 
  } // 리렌더링 발생

  // AXIOS UPDATE 기능구현 : CRUD (4) UPDATE
  const [update,setUpdate] = useState(false) // 조건부 렌더링
  const inputRef = useRef(null)
  const onUpdateHandler = () => {
    setUpdate(pre => !pre)
  }

  const [updatetodos, setUpdateTodos] = useState(title) // 수정할 title의 valuse
  const onChangeInput = (e) => {
    setUpdateTodos(e.target.value)
  }

  const onSubmitHandler = () => async (e) => {
    e.preventDefault()
    setUpdate(pre=>!pre)
  } // 리렌더링 발생

  useEffect(() => {
    update && inputRef.current.focus();
  }, [update]);


  return (
    <TodosBox>
        <button onClick={onDeleteHandler}>삭제</button>
        <button onClick={onUpdateHandler}>수정</button>
        <p>{title}</p>
        {update &&       
        <UpdateForm onSubmit={onSubmitHandler(todoid)} $state="update" $position={update}>
          <input ref={inputRef} value={updatetodos} type='text' onChange={onChangeInput} maxLength={10}/>
        </UpdateForm>}
    </TodosBox>

  )
}

const Form = styled.form`
  box-sizing: border-box;
  width: 500px;
  height: 50px;
  background-color:${({$state}) => $state === "update" ? "green" : "yellow"};
  display: flex;
  align-items: center;
  position: fixed;
  transition: 0.5ms linear;
  left: 0;
  bottom: 0;
  margin: 0;

  input {
    box-sizing: border-box;
    display: block;
    width: 100%;
    height: 30px;
  }
`

const UpdateForm = styled(Form)`
  bottom: ${({$position}) => $position ? "0" : "-50px"};
`


const TodosBox = styled.div`
  box-sizing: border-box;
  width: 300px;
  height: 40px;
  display: flex;
  padding: 0 10px;
  align-items: center;
  border-radius: 50px;
  border: 1px solid black;
  background-color: black;
  color: white;
  margin: 10px;
  gap:4px
`
