import React, { useEffect, useMemo, useState } from 'react'
import axios from 'axios'
import {styled} from 'styled-components'

const token = null

/* AXIOS ------------------------------------------------------------------ */
/* 네트워크 통신 라이브러리 
const instance = axios.create({
  baseURL:"http://localhost:3001/"
})

// instance.interceptors.request.use(
//   function (config) {
//     console.log("인터셉터 요청 성공!");
//     config.headers.Authorization = token ? `Bearer ${token}` : "";
//     return config
//   },
//   function (error) {
//     console.log("인터셉터 요청 오류!");
//     return Promise.reject(error)
//   } 
// )

// instance.interceptors.response.use(
//   function (config) {
//     console.log("인터셉터 응답 성공!");
//     return config
//   },
//   function (error) {
//     console.log("인터셉터 응답 오류!");
//     return Promise.reject(error)
//   } 
// )

/* App.js ------------------------------------------------------------------- */

function App() {
  const [todos, setTodos] = useState()
  const [newtodos, setNewTodos] = useState("")

  const MemoTodos = useMemo(()=> todos, [todos])

  const getTodos = async () => {
    const res = await instance.get("/todos")
    setTodos(res.data)
  }

  useEffect(()=>{
    getTodos()
  },[todos])

  const onChangeInput = (e) => {
    setNewTodos(e.target.value)
  }

  const onSubmitHandler = async (e) => {
    e.preventDefault()
    setTodos([...todos, {id:Date.now(), title:newtodos}])
    setNewTodos("")
    await instance.post("/todos", {id:Date.now(), title:newtodos})
  }

  return (
    <div>
      <p>JSON-Server</p>
      <Form onSubmit={onSubmitHandler}>
        <input value={newtodos} type='text' onChange={onChangeInput} maxLength={10}/>
      </Form>
      {todos && todos.map(({id, title}) => <Todos key={id} todoid={id} title={title} todos={todos} setTodos={setTodos}/>)}
    </div>
  )
}

export default App


const Todos = ({todoid, title, todos, setTodos}) => {

  const onDeleteHandler = async () => {
    await instance.delete(`/todos/${todoid}`)
    setTodos([...todos.filter(todo => todo.id !== {todoid})])
  }

  const [update,setUpdate] = useState(false)
  const [updatetodos, setUpdateTodos] = useState(title)

  const onUpdateHandler = () => {
    setUpdate(pre => !pre)
  } 

  const onChangeInput = (e) => {
    setUpdateTodos(e.target.value)
  }

  const onSubmitHandler = (todoid) => async (e) => {
    e.preventDefault()
    await instance.patch(`/todos/${todoid}`, {title:updatetodos})
    setTodos([...todos.map(todo => todo.id === todoid ? {...todo, title:updatetodos} : todo)])
    setUpdate(pre=>!pre)
  }

  return (
    <TodosBox>
        <button onClick={onDeleteHandler}>삭제</button>
        <button onClick={onUpdateHandler}>업데이트</button>
        {update 
          ? (<>
              <p>{title}</p>
              <Form onSubmit={onSubmitHandler(todoid)} $state="update">
                <input value={updatetodos} type='text' onChange={onChangeInput} maxLength={10}/>
              </Form>
            </>)
          : <p>{title}</p>}
    </TodosBox>
  )
}

const Form = styled.form`
  box-sizing: border-box;
  width: 500px;
  height: 50px;
  background-color:${({$state}) => $state === "update" ? "green" : "yellow"};
  display: flex;
  align-items: center;
  position: fixed;
  transition: 0.5ms linear;
  left: 0;
  bottom: 0;
  margin: 0;

  input {
    box-sizing: border-box;
    display: block;
    width: 100%;
    height: 30px;
  }
`


const TodosBox = styled.div`
  box-sizing: border-box;
  width: 300px;
  height: 40px;
  display: flex;
  padding: 0 10px;
  align-items: center;
  border-radius: 50px;
  border: 1px solid black;
  background-color: black;
  color: white;
  margin: 10px;
  gap:4px
`
