import React, {useEffect} from 'react'
import io from 'socket.io-client'

function App() {

  const socket = io.connet("http:/http://localhost:3000:3001")

  const sendMsg = () => {
    socket.emit("send_message", {message : "hello"})
  }

  useEffect(()=> {
    socket.on("receive_message", data => {
      alert(data.message)
    })
  }, [socket])
  return (
    <div>
      <h1>Socket io 테스트</h1>
      <input type='text'/> 
      <br/><button onClick={sendMsg}>메시지 보내기</button>
      <br/><button>채팅룸선택하기</button>
    </div>
  )
}

export default App
