import { Canvas, useFrame } from "@react-three/fiber";

function App() {

  return (
    <div>
      <h1>Three.js 다뤄보기</h1>
      <Canvas>
        <mesh 
          scale={active ? 1.5 : 1}
          onClick={()=> setActive(!active)}
          ref={myMesh}
        >
          <boxGeometry />
          <meshStandardMaterial color="royalblue"/>
        </mesh>
        <ambientLight intensity={0.1} />
        <directionalLight/>
      </Canvas>
    </div>
  );
}

export default App;


